import '../styles/main.css';
import React, {useState} from 'react'
import ReactTooltip from "react-tooltip";
import { ZoomableGroup, ComposableMap, Geographies, Geography } from "react-simple-maps";
import Article from "./Article.js"
import FlightsModal from "./FlightsModal.js"
import { Link } from "react-router-dom";
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css'
import articleData from "../data/articleData.js"
import airportData from "../data/airportData.js"
import countryData from "../data/countryData.js"

function Main() {
    const searchFlights = (e) => {
        e.preventDefault()
        const orig = document.getElementById("origin").value
        const dest = document.getElementById("destination").value
        const dep = document.getElementById("depart").value
        const depDate = new Date(dep)
        const ret = document.getElementById("return").value
        const retDate = new Date(ret)
        let error = false
        if (!orig) {
            setErrors({...errors, "origin": "Please select origin"})
            error = true
        }
        if (!dest) {
            setErrors({...errors, "destination": "Please select destination"})
            error = true
        }
        if (!dep) {
            setErrors({...errors, "depart": "Please select departure date"})
            error = true
        }
        if (!ret) {
            setErrors({...errors, "return": "Please select return date"})
            error = true
        }
        if (depDate > retDate) {
            setErrors({...errors, "dateRange": "Departure date must be before return date"})
        }
        if (!error) {
            setShow(true)
        }
    }

    const autocomplete = (inp, target) => {
        if (!target) return false
        removeAutocomplete()
        let autoList = document.createElement("div")
        autoList.className = "autocomplete-list"
        let parent = document.getElementById(inp.id + "-input-container")
        parent.appendChild(autoList)
        let items = document.createElement("div")
        items.className = "autocomplete-items"
        autoList.append(items)
        airportData.forEach(country => {
            if (target.toUpperCase() === country.name.substring(0, target.length).toUpperCase()) {
                country.airports.forEach(e => {
                    let item = document.createElement("div")
                    item.className= "autocomplete-item"
                    item.innerHTML = e
                    if (inp.id === "origin") {
                        item.onclick = () => setOrigin(e + ", " + country.name)
                    }
                    else {
                        item.onclick = () => setDest(e + ", " + country.name)
                    }
                    items.appendChild(item)
                })
            }
            document.onclick = () => {
                
            }
        })
    }

    const removeAutocomplete = () => {
        let list = document.getElementsByClassName("autocomplete-list")
        if (list.length) {
            for (let i of list) { i.remove() }
        }
    }

    const handleOrigin = (e) => {
        setOrigin(e.target.value)
        autocomplete(document.getElementById("origin"), e.target.value)
    }

    const handleDest = (e) => {
        setDest(e.target.value)
        autocomplete(document.getElementById("destination"), e.target.value)
    }
    
    const geoUrl = "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

    const [currCountry, setCountry] = useState("Australia")
    const [content, setContent] = useState("");
    const [destination, setDest] = useState("")
    const [origin, setOrigin] = useState("Australia")
    const today = new Date()
    const [depDate, setDepDate] = useState(today)
    const nextweek = new Date(today.getFullYear(), today.getMonth(), today.getDate()+7);
    const [retDate, setRetDate] = useState(nextweek)
    const [show, setShow] = useState(false)
    const [errors, setErrors] = useState({"origin": null, "destination": null, "depart": null, "return": null, "dateRange": null})
    const [data, setData] = useState(countryData[currCountry])

    let count = 0
    document.body.onmousedown = function(e) { if (e.button === 1) return false; }
    document.onclick = (e) => {
        e.stopImmediatePropagation()
        removeAutocomplete()
    }
    const width = 900
    const height = 600
    return (
        <div className="App">
            <FlightsModal show={show} setShow={setShow} destination={destination} origin={origin} retDate={retDate} depDate={depDate}></FlightsModal>
            <div id="map" className="map">
                <div >
                    <ComposableMap data-tip="" width={width} height={height}>
                        <ZoomableGroup translateExtent={[[0, 0], [width, height]]} zoom={1}>
                        <Geographies geography={geoUrl}>
                            {({ geographies }) =>
                            geographies.map(geo => (
                                <Geography
                                key={geo.rsmKey}
                                geography={geo}
                                onMouseEnter={() => {
                                    const { NAME } = geo.properties;
                                    setContent(`${NAME}`);
                                }}
                                onMouseLeave={() => {
                                    setContent("");
                                }}
                                style={{
                                    default: {
                                    fill: "#D6D6DA",
                                    outline: "none"
                                    },
                                    hover: {
                                    fill: "#FFA219",
                                    outline: "none"
                                    },
                                    pressed: {
                                    fill: "#FFA219",
                                    outline: "none"
                                    }
                                }}
                                onClick={(e) => {
                                    setCountry(geo.properties.NAME)
                                    setData(countryData[geo.properties.NAME])
                                    setDest(geo.properties.NAME)
                                    handleDest({"target": {"value": geo.properties.NAME}})

                                }}
                                onContextMenu={(e) => {
                                    e.preventDefault()
                                    setOrigin(geo.properties.NAME)
                                    handleOrigin({"target": {"value": geo.properties.NAME}})
                                }}
                                />
                            ))
                            }
                        </Geographies>
                        </ZoomableGroup>
                    </ComposableMap>
                </div>
                <ReactTooltip>{content}</ReactTooltip>
            </div>
            <div className="side-bar">
                <div className="flights">
                    <div className="header">Search Flights</div>
                    <form autoComplete="off" className="flight-form-container">
                        <div className="input-containers">
                            <label className="input-text">Flying From</label>
                            <div id="origin-container">
                                <div id="origin-input-container">
                                    <input id="origin" className="input-country" placeholder="Right Click on a country to select" value={origin} onClick={(e) => autocomplete(document.getElementById("origin"), e.target.value)} onChange={e => handleOrigin(e)}></input>
                                </div>
                                {errors.origin && <div className="error">{errors.origin}</div>}
                            </div>
                            <label className="input-text">Flying To</label>
                            <div id="destination-container">
                                <div id="destination-input-container">
                                    <input id="destination" className="input-country" placeholder="Left Click on a country to select" value={destination} onClick={(e) => autocomplete(document.getElementById("destination"), e.target.value)} onChange={e => handleDest(e)}></input>
                                </div>
                                {errors.destination && <div className="error">{errors.destination}</div>}
                            </div>
                            <label className="input-text">Depart Date</label>
                            <div>
                                <DatePicker id="depart" dateFormat="dd/MM/yyyy" className="input-date" selected={depDate} onChange={date => setDepDate(date)} />
                                {errors.depart && <div className="error">{errors.depart}</div>}
                            </div>
                            <label className="input-text">Return Date</label>
                            <div>
                                <DatePicker id="return" dateFormat="dd/MM/yyyy" className="input-date" selected={retDate} onChange={date => setRetDate(date)} />
                                {errors.return && <div className="error">{errors.return}</div>}
                                {errors.dateRange && <div className="error">{errors.dateRange}</div>}
                            </div>
                        </div>
                        <div className="country-end-button">
                            <button type="submit" className="button" onClick={e => searchFlights(e)}>Search</button>
                        </div>
                    </form>
                    
                </div>
                <div className="country-info">
                    <div className="header">Country Info</div>
                    <div className="country-container">
                        <div className="country-name">{currCountry}</div>
                        <div className="country-text">{data}</div>
                        <div className="country-end-button">
                            <Link to={"/restrictions?"+currCountry} className="button">Read More</Link>
                        </div>
                        <div className="country-name">Diseases</div>
                        <div className="disease-list">
                            <div>COVID-19</div>
                            <div>Measles</div>
                            <div>Rotavirus</div>
                            <div>Tetanus</div>
                        </div>
                    </div>
                </div>
                <div className="articles">
                    <div className="header">Articles</div>
                    <div className="articles-list">
                        {articleData.map(e => {
                            if (e.reports[0].locations[0] === currCountry) {
                                count += 1
                                return <Article data={e} key={count}></Article>
                            }
                            return null
                        })}
                        {articleData.map(e => {
                            if (e.reports[0].locations[0] === currCountry) {
                                count += 1
                                return <Article data={e} key={count}></Article>
                            }
                            return null
                        })}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Main;
